import React, { useEffect, useState } from 'react';

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Actualiza isLoggedIn cada vez que el componente se monta o se cambia el estado de localStorage
  useEffect(() => {
    const updateLoginStatus = () => {
      const loggedInStatus = localStorage.getItem("isLoggedIn");
      setIsLoggedIn(loggedInStatus === "true");
    };

    // Llama a la función para actualizar el estado inicial
    updateLoginStatus();

    // Agrega un listener para escuchar los cambios en localStorage
    window.addEventListener("storage", updateLoginStatus);

    // Limpia el listener cuando el componente se desmonta
    return () => {
      window.removeEventListener("storage", updateLoginStatus);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    setIsLoggedIn(false);
    window.location.href = "/";
  };

  return (
    <header className="header">
      <div className="container">
        <h1>CLAREMM</h1>
        <nav>
          <ul>
            <li><a href="/">Inicio</a></li>
            {isLoggedIn ? (
              <li><button onClick={handleLogout}>Cerrar sesión</button></li>
            ) : (
              <>
                <li><a href="/registro">Registro</a></li>
                <li><a href="/login">Login</a></li>
              </>
            )}
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
